-  DCHECK_NE(max_pic_order_cnt_lsb_, 0);
-  if ((pic_order_cnt_lsb < prev_pic_order_cnt_lsb) &&
-      (prev_pic_order_cnt_lsb - pic_order_cnt_lsb >=
-       max_pic_order_cnt_lsb_ / 2)) {
