-// Shift elements on the |v| starting from |from| to |to|, inclusive,
-// one position to the right and insert pic at |from|.
-static void ShiftRightAndInsert(H264Picture::PtrVector& v,
-                                int from,
-                                int to,
-                                H264Picture* pic) {
-  DCHECK(pic);
-  for (int i = to + 1; i > from; --i)
-    v[i] = v[i - 1];
-
-  v[from] = pic;
-}
