-        // and mark the current one instead.
-        curr_pic_->ref = true;
-        curr_pic_->long_term = true;
-        curr_pic_->long_term_frame_idx = ref_pic_marking->long_term_frame_idx;
-        break;
-      }
-
-      default:
-        // Would indicate a bug in parser.
-        NOTREACHED();
-    }
-  }
-
-  return true;
-}
