-bool VaapiH264Decoder::FinishPrevFrameIfPresent() {
-  // If we already have a frame waiting to be decoded, decode it and finish.
-  if (curr_pic_ != NULL) {
-    if (!DecodePicture())
-      return false;
-    return FinishPicture();
-  }
-
-  return true;
